<script lang="ts" setup>
import { useAuthenticationStore } from '@/stores/useAuthenticationStore';
import { useWebsocketStore } from '@/stores/useWebsocketStore';
import { storeToRefs } from 'pinia';
import { computed, onMounted, ref } from 'vue';

/** CONSTANTS */

const {me} = storeToRefs(useAuthenticationStore());

const bet = ref<number>(0);
const previousBet = ref<number>(0);

// numbers 0 to 36
const ALL_NUMBERS = new Array(37).fill(null).map((_, i) => i);
// odd numbers
const ODD_NUMBERS = ALL_NUMBERS.filter(number => number%2!==0);
// even number
const EVEN_NUMBERS = ALL_NUMBERS.filter(number => number%2===0);

// 1 to 12
const NUMBERS_1_TO_12 = ALL_NUMBERS.slice(1, 13);
// 13 to 24
const NUMBERS_13_TO_24 = ALL_NUMBERS.slice(13, 25);
// 25 to 36
const NUMBERS_25_TO_36 = ALL_NUMBERS.slice(25);

// red numbers
const RED_NUMBERS = [3, 2, 5,1,7,9,12,18,21,27,30,36,14,23,32,16,19,25,34];
// black numbers
const BLACK_NUMBERS = ALL_NUMBERS.filter(number => !RED_NUMBERS.includes(number) && number !== 0);

// 1 to 18
const NUMBERS_1_TO_18 = ALL_NUMBERS.slice(1, 19);
// 19 to 36
const NUMBERS_19_TO_36 = ALL_NUMBERS.slice(19);

// table lines (row by row)
const TABLE_LINES = [
    [3,6,9,12,15,18,21,24,27,30,33,36],
    [2,5,8,11,14,17,20,23,26,29,32,35],
    [1,4,7,10,13,16,19,22,25,28,31,34]
];

const TABLE_CUSTOM_SIGNS_MAPPER: Record<string, number[]> = {
    '2_to_1_1': TABLE_LINES[0],
    '2_to_1_2': TABLE_LINES[1],
    '2_to_1_3': TABLE_LINES[2],
    '1_12': NUMBERS_1_TO_12,
    '13_24': NUMBERS_13_TO_24,
    '25_36': NUMBERS_25_TO_36,
    '1_to_18': NUMBERS_1_TO_18,
    '19_to_36': NUMBERS_19_TO_36,
    'red': RED_NUMBERS,
    'black': BLACK_NUMBERS,
    'even': EVEN_NUMBERS,
    'odd': ODD_NUMBERS
}
/** CONSTANTS */

const refs = ref<Record<string, HTMLElement>>({});

const pickedChoice = ref<string>("");
const pickedChoiceElements = computed(() => {
    if(!pickedChoice.value.trim()) {
        return []
    }
    return TABLE_CUSTOM_SIGNS_MAPPER[pickedChoice.value] ?? [pickedChoice.value]
})

const createHoverEvent = (refName: string, elements: number[]) => {
    const elements2Disable = ALL_NUMBERS.filter(el => !elements.includes(el));
    refs.value[refName].onmouseenter = () => {
        pickedChoiceElements.value.forEach(el => refs.value[el].classList.remove('disabled'));
        elements2Disable.forEach(el => refs.value[el].classList.add('disabled'));
        elements.forEach(el => refs.value[el].classList.remove('disabled'));
    }
    refs.value[refName].onmouseleave = () => {
        elements.forEach(el => refs.value[el].classList.add('disabled'));
        pickedChoiceElements.value.forEach(el => refs.value[el].classList.remove('disabled'));

        if(!pickedChoiceElements.value.length) {
            ALL_NUMBERS.forEach(el => refs.value[el].classList.remove('disabled'));
        }
    }
}

const verifyNumberInputEntry = (event: Event) => {
    const target = event.target! as HTMLInputElement;
    if(target) {
        const value = parseFloat(target.value);
        if(value > me.value?.zipetteCoins!) {
            bet.value = me.value?.zipetteCoins!;    
        } else if(value < 1) {
            bet.value = 0;
        }
    }
}

const reset = () => {
    resetUserSelection();
    useWebsocketStore().send({
        type: "USER_BET_REMOVED"
    })
}

const allIn = () => {
    bet.value = me.value?.zipetteCoins!;
}

const giveMoneyBack = () => {
    me.value!.zipetteCoins = me.value!.zipetteCoins + bet.value;
}

const resetUserSelection = () => {
    pickedChoice.value = "";
    bet.value = 0;
    ALL_NUMBERS.forEach(el => refs.value[el].classList.remove('disabled'));
}

onMounted(() => {
    for(const el of Array.from(document.querySelectorAll('g[id]'))) {
        const element = el as HTMLElement;
        const id = el.id; 

        refs.value[id] = element;
        element.onclick = () => {
            pickedChoice.value = id;
            
            if(previousBet.value !== bet.value) {
                me.value!.zipetteCoins -= bet.value;
                previousBet.value = bet.value;
            }

            useWebsocketStore().send({
                type: "USER_SELECTION",
                data: {
                    selection: id,
                    bet: bet.value
                }
            });

            ALL_NUMBERS.forEach(el => refs.value[el].classList.add("disabled"));
            pickedChoiceElements.value.forEach(el => refs.value[el].classList.remove("disabled"));
        }
    }

    for(const [key, elements] of Object.entries(TABLE_CUSTOM_SIGNS_MAPPER)) {
        createHoverEvent(key, elements);
    }
});

defineExpose({ resetUserSelection, giveMoneyBack });
</script>

